<?php// Disciplina : Desenvolvimento WEB // Walnerclass RequestController{	const VALID_METHODS = array('GET', 'POST', 'PUT', 'DELETE');	const VALID_PROTOCOL = array('HTTP', 'HTTPS');	const VALID_PROT_VERSAO = array('1.0','1.1','1.2','1.3');		public function create_request($request_info)	{		if(!self::is_valid_method($request_info['REQUEST_METHOD']))		{			return array("code" => "405", "message" => "method not allowed");					}					if(!self::is_valid_protocol($request_info['SERVER_PROTOCOL']))		{			return array("code" => "418", "message" => "I'm a teapot");					}		if(!self::is_valid_prot_versao($request_info['SERVER_PROTOCOL']))		{			return array("code" => "505 ", "message" => "HTTP Version Not Supported");					}				if(!self::is_valid_queryString($request_info['QUERY_STRING']))		{			return VALID_QUERRY;			//return array("code" => "510 ", "message" => "Not Extended");					}	//	$request_info['REQUEST_METHOD'];  // feito	//	$request_info['SERVER_PROTOCOL'];  // feito	//	$request_info['REMOTE_ADDR'];  // nao precisa fazer	//	$request_info['SERVER_ADDR'];  // nao precisa fazer	//	$request_info['REQUEST_URI'];	//	$request_info['QUERY_STRING'];	//	file_get_contents('php://input');			//	return new Request();			}		public function is_valid_method($method)	{		if( is_null($method) || !in_array($method, self::VALID_METHODS))			return false;				return true;	}	public function is_valid_protocol($protocol)	{		if( is_null($protocol) || !in_array(substr($protocol,0,4), self::VALID_PROTOCOL))			return false;				return true;	}	public function is_valid_prot_versao($protocol)	{		$protocolArray= explode("/", $protocol);		if( is_null($protocol) || !in_array(substr($protocolArray[1],0,3), self::VALID_PROT_VERSAO))			return false;				return true;	}	public function is_valid_queryString($querryString)	{		if ( isset($querryString["var"]) && count($querryString) > 1 )     		return false;    	return true;	}}